package StringDemo;

public class StringBufferDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		

		String s= "Rina";
		StringBuffer s1= new StringBuffer("Shamika");
		System.out.println(s.replace('R', 'M'));
		
		System.out.println(s);
		System.out.println(s1.replace(0, 3, "Vha"));
		System.out.println(s1);

		

	}

}
